mapper.insert:
INSERT INTO TBL_COMMON_REPLY (
	REPLY_NO
	,MEM_NO
	,TYPE_CODE
	,ITEM_NO
	,REPLY_CONTENT
	,REPLY_WRITER_NAME
	,REPLY_WRITE_DATE
)VALUES(
	TBL_COMMON_REPLY_REPLY_NO_SEQ.nextval
	,#{mem_no}
	,#{type_code}
	,#{item_no}
	,#{reply_content}
	,(SELECT MEM_NAME FROM TBL_MEMBER WHERE MEM_NO=#{mem_no})
	,SYSDATE
)

mapper.delete:
DELETE FROM 
	TBL_COMMON_REPLY
WHERE
	REPLY_NO=#{reply_no}
AND
	TYPE_CODE=#{type_code}
AND
	ITEM_NO=#{item_no}
AND
	MEM_NO=#{mem_no}

mapper.update:
UPDATE 
	TBL_COMMON_REPLY
SET
	REPLY_CONTENT=#{reply_content}
WHERE
	REPLY_NO=#{reply_no}
AND
	TYPE_CODE=#{type_code}
AND
	ITEM_NO=#{item_no}
AND
	MEM_NO=#{mem_no}
