mapper.delete:
DELETE FROM 
	TBL_COMMON_REPLY
WHERE
	REPLY_NO=#{reply_no}
AND
	TYPE_CODE=#{type_code}
AND
	ITEM_NO=#{item_no}
AND
	MEM_NO=#{mem_no}

mapper.update:
UPDATE 
	TBL_COMMON_REPLY
SET
	REPLY_CONTENT=#{reply_content}
WHERE
	REPLY_NO=#{reply_no}
AND
	TYPE_CODE=#{type_code}
AND
	ITEM_NO=#{item_no}
AND
	MEM_NO=#{mem_no}
