mapper.select:
select count(*)
from tbl_accompany
where use_yn=#{use_yn}

	and accompany_status_code=#{accompany_status_code}

	and (title like '%'||#{searchValue, jdbcType=VARCHAR}||'%' 
	or content like '%'||#{searchValue, jdbcType=VARCHAR}||'%')

select accompany.rnum, accompany.accompany_no, accompany.mem_no, accompany.write_date, accompany.start_date, accompany.end_date, accompany.title, accompany.content, accompany.attach_file, accompany.accompany_status_code, accompany.gender_code, accompany.use_yn, accompany.hits, member.mem_name, ( select count(*) from tbl_common_reply where item_no=accompany.accompany_no and type_code='S0010' ) as reply_count from (select * from ( SELECT ROWNUM rnum, b.* FROM ( SELECT * FROM tbl_accompany where use_yn=#{use_yn}
and accompany_status_code=#{accompany_status_code}
and (title like '%'||#{searchValue, jdbcType=VARCHAR}||'%' or content like '%'||#{searchValue, jdbcType=VARCHAR}||'%')
ORDER BY accompany_no desc )b ) where rnum >= #{startRow} and rnum <= #{endRow} ) accompany, tbl_member member where accompany.mem_no = member.mem_no

mapper.update:
update tbl_accompany
set hits=hits + 1
where accompany_no = #{accompany_no}

mapper.select:
select count(*) 
from tbl_accompany
where accompany_no=#{accompany_no}
and mem_no=#{mem_no}

mapper.update:
update tbl_accompany
set use_yn=#{use_yn}
where accompany_no=#{accompany_no}
and mem_no=#{mem_no}
