mapper.select:
SELECT * FROM TBL_ADMIN_MAIN_SETTING WHERE SETTING_NO=( SELECT MAX(SETTING_NO) FROM TBL_ADMIN_MAIN_SETTING WHERE USE_YN=#{use_yn} AND SETTING_TYPE_CODE=#{setting_type_code}
AND SETTING_SPOT_CODE=#{setting_spot_code}
)

SELECT
	*
FROM
	TBL_ADMIN_MAIN_SETTING
WHERE
	USE_YN=#{use_yn}
AND
	SETTING_TYPE_CODE=#{setting_type_code}

AND
	SETTING_SPOT_CODE=#{setting_spot_code}
