	TBL_MESSAGE(
		  MSG_NO
		, SEND_MEM_NO
		, MSG_GROUP_NO
		, RECV_MEM_NO
		, MESSAGE
		, SEND_TIME
) VALUES(
	TBL_MESSAGE_MSG_NO_SEQ.nextval
	,#{send_mem_no}
	,#{msg_group_no}
	,#{recv_mem_no}
	,#{message}
	,SYSDATE
)

mapper.update:
