mapper.update:
update tbl_planner set title=#{title},
						attach_file=#{attach_file, jdbcType=VARCHAR},
						memo=#{memo, jdbcType=VARCHAR},
						start_date=#{start_date},
						end_date=#{end_date}
where planner_no=#{planner_no}

update tbl_item set item_order=#{item_order},
					spot_no=#{spot_no},
					attach_photoes=#{attach_photoes, jdbcType=VARCHAR},
					note=#{note, jdbcType=VARCHAR},
					item_time=#{item_time, jdbcType=VARCHAR}
where item_no=#{item_no}

update tbl_money set money_type_code=#{money_type_code},
					price=#{price},
					money_title=#{money_title, jdbcType=VARCHAR}
where money_no=#{money_no}

mapper.select:
select * from tbl_planner

	mem_no=#{mem_no}


		AND use_yn=#{use_yn}


		AND use_yn!=#{use_yn}

SELECT BB.* FROM (SELECT ROWNUM rnum , AA.* FROM (SELECT PLA.PLANNER_NO , PLA.MEM_NO , PLA.PLANNER_BA_CODE , PLA.TITLE , (SELECT SAVE_NAME||'.'||EXTENSION FROM TBL_COMMON_FILE WHERE FILE_NO=PLA.ATTACH_FILE)ATTACH_FILE , PLA.MEMO , PLA.START_DATE , PLA.END_DATE , PLA.REG_DATE , PLA.USE_YN , MEM.MEM_NAME , MEM.MEM_EMAIL , (SELECT SAVE_NAME||'.'||EXTENSION FROM TBL_COMMON_FILE WHERE FILE_NO=MEM.MEM_PROFILE_PHOTO) MEM_PROFILE_PHOTO , (SELECT COUNT(1) FROM TBL_SWEET WHERE PLANNER_NO=PLA.PLANNER_NO)sweet_count , (SELECT COUNT(1) FROM TBL_COMMON_REPLY WHERE ITEM_NO=PLA.PLANNER_NO AND TYPE_CODE=PLA.PLANNER_BA_CODE)reply_Count , (SELECT COUNT(1) FROM TBL_BOOKMARK WHERE ITEM_NO=PLA.PLANNER_NO AND BOOKMARK_TYPE_CODE=PLA.PLANNER_BA_CODE)bookmark_Count FROM TBL_PLANNER PLA, TBL_MEMBER MEM WHERE PLA.USE_YN='Y0001' AND PLA.MEM_NO= MEM.MEM_NO
AND PLA.PLANNER_BA_CODE=#{searchCondition1}
AND PLA.TITLE LIKE '%'||#{searchWord1}||'%'
ORDER BY
reply_Count desc
bookmark_Count desc
sweet_count desc
PLA.REG_DATE desc
PLA.PLANNER_NO DESC
)AA )BB WHERE BB.rnum >= #{startRow} AND BB.rnum <= #{endRow}

SELECT count(*) FROM (SELECT ROWNUM rnum , AA.* FROM (SELECT PLA.PLANNER_NO , PLA.MEM_NO , PLA.PLANNER_BA_CODE , PLA.TITLE , (SELECT SAVE_NAME||'.'||EXTENSION FROM TBL_COMMON_FILE WHERE FILE_NO=PLA.ATTACH_FILE)ATTACH_FILE , PLA.MEMO , PLA.START_DATE , PLA.END_DATE , PLA.REG_DATE , PLA.USE_YN , MEM.MEM_NAME , MEM.MEM_EMAIL , (SELECT SAVE_NAME||'.'||EXTENSION FROM TBL_COMMON_FILE WHERE FILE_NO=MEM.MEM_PROFILE_PHOTO) MEM_PROFILE_PHOTO , (SELECT COUNT(1) FROM TBL_SWEET WHERE PLANNER_NO=PLA.PLANNER_NO)sweet_count , (SELECT COUNT(1) FROM TBL_COMMON_REPLY WHERE ITEM_NO=PLA.PLANNER_NO AND TYPE_CODE=PLA.PLANNER_BA_CODE)reply_Count , (SELECT COUNT(1) FROM TBL_BOOKMARK WHERE ITEM_NO=PLA.PLANNER_NO AND BOOKMARK_TYPE_CODE=PLA.PLANNER_BA_CODE)bookmark_Count FROM TBL_PLANNER PLA, TBL_MEMBER MEM WHERE PLA.USE_YN='Y0001' AND PLA.MEM_NO= MEM.MEM_NO
AND PLA.PLANNER_BA_CODE=#{searchCondition1}
AND PLA.TITLE LIKE '%'||#{searchWord1}||'%'
)AA )BB
