mapper.select:

select * from (select rownum rnum, a.* from
			  (select * from tbl_visitor where mate_mem_no=#{mate_mem_no} order by visitor_no desc) a)
where rnum >= #{startRow} and rnum <= #{endRow} and visitor_status_code=#{visitor_status_code}

mapper.insert:
insert into tbl_visitor 
values(
tbl_visitor_visitor_no_seq.nextval
, #{mem_no}
, #{visitor_content}
, sysdate
, #{visitor_status_code}
, #{mate_mem_no}
)
